mapper.select:
SELECT  admin_id AS id,
		admin_name AS name
FROM tb_admin
WHERE admin_id = #{id}
AND admin_password = #{pwd}
AND use_yn = 'Y'

SELECT event_file_group_seq
FROM tb_event
WHERE event_seq = #{eventSeq}

mapper.update:
UPDATE tb_file_attr
SET use_yn = 'N',
	update_date = CURRENT_TIMESTAMP(),
	update_id = #{memberId}
WHERE file_group_seq = #{fileGroupSeq}
AND file_seq in (${deleteFiles})
